function generateProfessionData(title) {
  const slug = title.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/^-|-$/g, '');
  
  const genericSkills = [
    'Communication Skills', 'Problem Solving', 'Team Collaboration',
    'Time Management', 'Project Management', 'Analytical Thinking',
    'Attention to Detail', 'Adaptability', 'Continuous Learning',
    'Professional Development'
  ];
  
  const genericResponsibilities = [
    `Execute ${title.toLowerCase()} responsibilities with precision and professionalism`,
    'Collaborate effectively with team members and stakeholders',
    'Maintain high standards of quality and performance',
    'Stay updated with industry trends and best practices',
    'Document processes and maintain accurate records',
    'Contribute to team goals and organizational objectives',
    'Ensure compliance with relevant regulations and standards'
  ];
  
  return {
    slug,
    title,
    skills: genericSkills,
    responsibilities: genericResponsibilities
  };
}

const allProfessions = professions.map(generateProfessionData);
const outputPath = path.join(process.cwd(), 'public', 'data', 'occupations.json');
fs.writeFileSync(outputPath, JSON.stringify(allProfessions, null, 2), 'utf8');

